/

BEGIN 
EXECUTE IMMEDIATE 'CREATE TABLE 
"DETALLES_VENTA" (
    "Id" NUMBER(10) GENERATED BY DEFAULT ON NULL AS IDENTITY NOT NULL,
    "VentaId" NUMBER(10) NOT NULL,
    "ProductoId" NUMBER(10) NOT NULL,
    "Cantidad" NUMBER(10) NOT NULL,
    "PrecioUnitario" NUMBER(18,2) NOT NULL,
    "Subtotal" NUMBER(18,2) NOT NULL,
    CONSTRAINT "PK_DETALLES_VENTA" PRIMARY KEY ("Id"),
    CONSTRAINT "FK_DETALLES_VENTA_VENTAS_VentaId" FOREIGN KEY ("VentaId") REFERENCES "VENTAS" ("Id") ON DELETE CASCADE
)';
END;
/

